class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
       int n = v.size();
        int ans = 0;
        if(k == 1)return n;
        int l = 1;
        int i = 1;
        while(i < n + k -1){
            int curr = i;
            int pre = i - 1;
            if(curr >= n){
                curr-=n;
            }
            if(pre >= n){
                pre -= n;
            }
            if(v[curr] != v[pre]){
                l++;
            }
            else l = 1;
            if(l >= k)ans++;
            i++;
        }
        return ans;
    }
};
