class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n=colors.size(); 
        int ans=0; 

        int len= 1;  
        if (k==1) { 
            return n; 
        } 

        int i = 1;
        while (i<colors.size()+k-1) { 
            int prev=(i-1)%n; 
            int curr= i%n; 
            if (colors[curr]!=colors[prev]) { 
                len++; 
            }

            else { 
                len= 1; 
            } 



            if (len>=k) { 
                ans++; 
            } 

            i++; 
        } 

        return ans; 
    } 
};
