class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        for (int i = 0, j = 1; i < n;) {
            bool match = true;
            // cout << i << ends << j << endl;
            for (; j - i + 1 <= k; j++) {
                if (colors[j % n] == colors[(j - 1) % n]) {
                    match = false;
                    break;
                } 
            }
            if (match) {
                ans++;
                i++;
                j = i + k - 1;
            } else {
                i = j;
                j = i + 1;
            }
        }

        return ans;
    }
};
