class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),now=0,ans=0;
        vector<int> b(n, 0);
        for(int i=1;i<n;++i) if(colors[i]!=colors[i-1]) b[i]=1;
        if(colors[0]!=colors[n-1]) b[0]=1;
        for(int i=n-k+1;i<n;++i) now+=b[i];
        // if(now==k) 
        for(int i=0;i<n;++i){
            now+=b[i]-b[(i-k+1+n)%n];
            if(now==k-1) ++ans;
            // cout<<ans<<'?';
        }
        // cout<<endl;
        return ans;
    }
};
