class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& tile_colors, int group_length) {
        int num_tiles = tile_colors.size();
        int group_count = 0;

        if (group_length == 1) {
            return countSingleTileGroups(tile_colors, num_tiles);
        }

        int current_length = 1;
        int index = 1;
        while (index < num_tiles + group_length - 1) {
            current_length = calculateCurrentLength(tile_colors, index, current_length, group_length, num_tiles);
            group_count = updateGroupCount(current_length, group_length, group_count);
            ++index;
        }

        return group_count;
    }

private:
    int countSingleTileGroups(vector<int>& tile_colors, int num_tiles) {
        return num_tiles;
    }

    int calculateCurrentLength(vector<int>& tile_colors, int index, int current_length, int group_length, int num_tiles) {
        int current_index = handleCircularIndexing(index, num_tiles);
        int previous_index = handleCircularIndexing(index - 1, num_tiles);

        if (tile_colors[current_index] != tile_colors[previous_index]) {
            ++current_length;
        } else {
            current_length = 1;
        }

        return current_length;
    }

    int handleCircularIndexing(int index, int num_tiles) {
        if (index >= num_tiles) {
            return index - num_tiles;
        }
        return index;
    }

    int updateGroupCount(int current_length, int group_length, int group_count) {
        if (current_length >= group_length) {
            ++group_count;
        }
        return group_count;
    }
};
