class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans = 0;
        int start = 0;
        int end = 1;
        while (end < n) {
            if (colors[end] == colors[end-1]) {
                start = end;
            }
            if (end - start + 1 >= k) ans++;
            end++;
        }
        if (colors[n-1] == colors[0]) return ans;
        if (end - start + 1 >= k) ans++;
        end = 1;
        while (end < k-1) {
            if (colors[end] == colors[end-1]) {
                return ans;
            }
            if (end + n - start + 1 >= k) ans++;
            end++;
        }
        return ans;
    }
}
