class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):
        n=len(colors)
        new_col=[0]*(n-1+k)
        for i in range(n):
            new_col[i]=colors[i]
        for i in range(k-1):
            new_col[n+i]=colors[i]
        res=0
        m=1
        p=len(new_col)
        for i in range(1,p):
            if new_col[i]!=new_col[i-1]:
                m+=1
            else:
                m=1
            if m>=k:
                res+=1
        return res
