class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> record(colors.size(), 0);
        int total_sum = 0;
        int res = 0;

        if(colors[0] != colors.back()) record[0] = 1;
        for(int i = 1; i < colors.size(); ++i) {
            if(colors[i] != colors[i - 1]) record[i] = 1;
        }

        for(int i = 0; i + 1 < k; ++i) total_sum += record[i];

        for(int i = 0; i < colors.size(); ++i) {
            total_sum += record[(i + k - 1) % colors.size()] - record[i];
            if(total_sum == k - 1) ++res;
        }

        return res;
    }
};
