class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        colors.insert(colors.end(),colors.begin(),colors.begin()+k);
        int p=colors.size();
        int cnt=0;

        unordered_map<string,int> ctx;
        unordered_map<int,int> mpp;
        vector<int> prev_difference(p+1,0);
        for(int i=1;i<p;i++)
        {
            prev_difference[i]+=prev_difference[i-1]+abs(colors[i]-colors[i-1]);
        }
        k--;

        for(int i=k+1;i<p;++i)
        {
            if((prev_difference[i]-prev_difference[i-k])==k)
            {
                cnt++;
            }
        }
        return cnt;
    }
};
