class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        prefix = [0]*(2*n)
        for i in range(2*n):
            prefix[i] = prefix[i-1]
            if colors[i%n] == colors[(i-1)%n]:
                prefix[i] += 1
        res = 0
        for i in range(n):
            if prefix[i] == prefix[i+k-1]:
                res += 1
        return res
