func numberOfAlternatingGroups(a []int, k int) (res int) {
    a = append(a, a...)
    cnt := 0
    for i := 0; i < len(a) / 2 + k - 1; i++ {
        if i > 0 && a[i] != a[i - 1] {
            cnt ++
        }
        if i >= k - 1 && cnt == k - 1 {
            res ++
        }
        if i >= k - 1 && a[i - k + 1] != a[i - k + 2] {
            cnt --
        }
    }
    return
}
