class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        if(k == 1) return n;
        int length = 1;
        int i = 1;
        while(i < n + k - 1){
            int curr = i % n;
            int prev = (i - 1) % n;
            if(colors[curr] != colors[prev]){
                length++;
            }
            else{
                length = 1;
            }
            if(length >= k){
                ans++;
            }
            i++;
        }
        return ans;
    }
};
