class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i=0,j=1,ans=0,n=colors.size();
        while(i<n){
            if(colors[(j+n) % n] != colors[(j+n+1) % n] && colors[(j+n-1) % n] == colors[(j+n+1) % n]){
                j++;
                if(j-i+1 == k) {
                    ans++;
                    i++;
                }
            }
            else{
                i=j;
                j++;
            }
        }
        return ans;
    }
};
