class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors[1-k:] + colors
        cnt = 1
        ans =0
        pre = colors[0]
        for c in colors[1:]:
            if c == pre:
                cnt = 1
            else:
                cnt +=1
                if cnt == k:
                    ans+=1
                    cnt -= 1
            pre = c
        return ans
