class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int totalTiles = colors.size();
        int alternatingTiles = 0;
        int i = 0, j = 1;

        while (j < totalTiles + k && i < totalTiles) {
            int temp_j = j;
            int temp_end = temp_j % totalTiles;

            while (colors[temp_end] != colors[(temp_end - 1 + totalTiles) % totalTiles] && (temp_j < (totalTiles + k - 1))) {
                temp_j++;
                temp_end = temp_j % totalTiles;
            }

            alternatingTiles += max(temp_j - i - k + 1, 0);
            i = temp_j;
            j = i + 1;
        }

        return alternatingTiles;
    }
};
