class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> nums=colors;
        for(int i=0;i<k-1;i++)
            nums.push_back(colors[i]);
        int cnt=0;
        for(int i=0;i<nums.size();i++)
        {
            int tt=nums[i];
            int cc=0;
            while(i<nums.size() && nums[i]==tt)
            {
                i++;
                tt=1-tt;
                cc++;
            }
            i--;
            if(cc>=k)
            {
                cnt+=(cc-k+1);
            }
        }
        return cnt;
    }
};
