class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        for (int i = 0; i < k - 1; i++)
        {
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        vector<int> cont;
        int temp = 1;
        for (int i = 1; i < n; i++)
        {
            if (colors[i] != colors[i - 1])
            {
                temp++;
            }
            else
            {
                cont.push_back(temp);
                temp = 1;
            }
        }
        if (temp) cont.push_back(temp);
        n = cont.size();
        int ans = 0;
        for (int i = 0; i < n; i++)
        {
            if (cont[i] >= k) ans += (cont[i] - k + 1);
        }
        return ans;
    }
};
