class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;++i)
        {
            colors.push_back(colors[i]);
        }
        int l=0;
        int r=1;
        int ans=0;
        //dw qejwhgdewi 
        while(r<colors.size())
        {
            // cout<<j<<" "<<i;
            int x=colors[l];
            while(r<colors.size() && x!=colors[r])
            {
                x=1-x;
                r++;
            }
            int len = r-l-k+1;
            // cjkbnwdjbc iohedw 
            ans+=max(0,len);
            l=r;
            r++;
            // jeqgw dwqku
        }

        return ans;








    }
};
