class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;i++)
        colors.push_back(colors[i]);
        int mark=n-1;
        int l=0,r=1, count=0;
        while(l<=mark && r<colors.size())
        {
            if(colors[r]!=colors[r-1] && colors[r]!=colors[r+1])
            {
                if(r-l+2==k)
                {
                    count++;
                    l++;
                }
                r++;
            }
            else
            {
                l=r;
                r++;
            }
        }
        return count;
    }
};
