class Solution {
       public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans =0;
        int n = colors.length;
        for(int i = 0; i < n;i++){
            int index = i+1;
            int cnt = 1;
            while(index < n && colors[index] != colors[index-1]){
                index++;
                cnt++;
            }
            i = index-1;
            ans += Math.max(cnt-k+1,0);
        }
        int prev = 99;
        int cnt = 0;
        for(int i = n-k+1; i < n+k-1 ;i++){
            System.out.println((i%n));
            if(prev == 99){
                prev = colors[i%n];
                cnt++;
            }else if(colors[i%n]!= colors[(i-1)%n] ){
                prev = colors[i%n];
                cnt++;
            }else {
                ans += Math.max(cnt-k+1,0);
                cnt = 1;
                prev  = colors[i%n];
            }
        }
                           ans += Math.max(cnt-k+1,0);

        return ans;
    }
}
