class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):
        """
        :type colors: List[int]
        :type k: int
        :rtype: int
        """
        a=[]
        n=len(colors)
        for i in range(n-1):
            if colors[i]!=colors[i+1]:
                a.append(1)
            else:
                a.append(0)
        if colors[n-1]!=colors[0]:
            a.append(1)
        else:
            a.append(0)
        r=0
        z=[]
        t=0
        for i in range(n):
            if a[i]==1:
                t+=1
            else:
                z.append(t)
                t=0
        if len(z)==0:
            z.append(t)
        else:
            z[0]+=t
        for w in z:
            r+=max(0,w-k+2)
        if z[0]==n:
            return n
        return r
