class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size_col_arr = colors.size();
        int count_col = 0;

        if (k == 1) {
            return size_col_arr;
        }

        int col_alternating_length = 1;
        int i = 1;
        while (i < size_col_arr + k - 1) {
            int current_col_alt = i;
            int previous_col_arr = i - 1;

            if (current_col_alt >= size_col_arr) {
                current_col_alt -= size_col_arr;
            }
            if (previous_col_arr >= size_col_arr) {
                previous_col_arr -= size_col_arr;
            }

            if (colors[current_col_alt] != colors[previous_col_arr]) {
                ++col_alternating_length;
            } 
            else {
                col_alternating_length = 1;
            }
            if (col_alternating_length >= k) {
                ++count_col;
            }

            ++i;
        }

        return count_col;

    }
};
