class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        n = len(a)
        if all(a[i] != a[(i + 1) % n] for i in range(n)): return n
        @cache
        def dfs(i):
            j = (i + 1) % n
            if a[i] == a[j]: return 1
            return 1 + dfs(j)
        res = sum(dfs(i) >= k for i in range(n))
        dfs.cache_clear()
        return res
