public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        int result = 0;
        HashSet<int> b = new ();
        for (int i = 0; i < colors.Length; i++) {
            int p = (i - 1 + colors.Length) % colors.Length;
            if (colors[p] == colors[i]) {
                b.Add(i);
            }
        }
        int c = 0;
        for (int r = 0; r < colors.Length + k - 1; r++) {
            if (b.Contains(r % colors.Length)) {
                c++;
            }
            if (r >= k - 1) {
                int l = r - k + 1;
                if (b.Contains(l)) {
                    c--;
                }
                if (c == 0) {
                    // Console.WriteLine($"r = {r}");
                    result++;
                }
            }
        }
        return result;
    }
}
