class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        ll ans = 0;
        if(k == 1) return n; //for singll legth every element is solo
        int lenSoFar = 1,idx = 1;

        while(idx < n + k - 1) {
            ll c = idx,p = idx - 1;
            if(c >= n) c -= n;
            if (p >= n) p -= n;

            colors[c] != colors[p] ? lenSoFar++ : lenSoFar = 1;

            if(lenSoFar >= k) ans++;
            idx++;
        }
        return ans;
    }
};
