    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int a=0;
        int i=0;
        int ans=0;
        int h=0;
        while(i<k-1){
            if(colors[i]==colors[i+1]){
                h++;
            }

            i++;
        }

        int l=0;
        while(l<colors.size()){

            if(colors[i%colors.size()]==colors[(i+1)%colors.size()]){
                h++;
            }
            if(colors[l%colors.size()]==colors[(l+1)%colors.size()]){
                h--;
            }

                    if(h==0){
            ans++;
        }
        l++;
        i++;

        }
