class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int j) {
        col.insert(col.end(),col.begin(),col.begin()+j-1);

        int e = 0, p = 1;

        for(size_t i=1;i<col.size();i++){
            p = (col[i] != col[i-1]) ? p+1 : 1;
            if( p >= j){
                ++e;
            }
        }
        return e;
    }
};
