class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> vec;
        int p = colors[0], s = 0;
        for(int i = 1; i < colors.size(); i++){
            if(colors[i] != p){
                s++;
            }
            else{
                vec.push_back(s+1);
                s = 0;
            }
            p = colors[i];
        }
        vec.push_back(s+1);
        int ans = 0;
        for(auto i:vec){
            if(i >= k){
                ans += i - k + 1;
            }
        }
        if(colors[colors.size()-1] == colors[0] || vec[vec.size()-1] + vec[0] < k){
            return ans;
        }
        int v = min(vec[0], k-1);
        int m = k - v;
        while(v > 0 && vec[vec.size()-1] >= m){
            ans++;
            v--;
            m++;
        }
        return ans;
    }
};
