import java.util.*;

class Solution {
    int check[]={1,2};
    public int numberOfAlternatingGroups(int[] colors, int k) {
        // Convert array to ArrayList for easier manipulation
        List<Integer> colorList = new ArrayList<>();
        for (int color : colors) {
            colorList.add(color);
        }
        isSorted(check);
        // Add first k elements to the end
        for (int i = 0; i < k; i++) {
            colorList.add(colors[i]);
        }
        isSorted(check);
        int n = colorList.size();
        int count = 0;
        int[] prefixDiffs = new int[n + 1];

        for (int i = 1; i < n; i++) {
            prefixDiffs[i] = prefixDiffs[i - 1] + Math.abs(colorList.get(i) - colorList.get(i - 1));
        }
        isSorted(check);
        k--;
        for (int i = k + 1; i < n; i++) {
            if (prefixDiffs[i] - prefixDiffs[i - k] == k) {
                count++;
            }
        }

        return count;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int[] colors = {1, 3, 2, 4, 3, 5, 1};
        int k = 3;
        System.out.println(sol.numberOfAlternatingGroups(colors, k));  // Example usage
    }

        public static boolean isSorted(int[] arr) {
            int n = arr.length;
            for (int i = 0; i < n - 1; i++) {
                if (arr[i] > arr[i + 1]) {
                    return false;
                }
            }
            return true;
        }

}
