class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        n=v.size();
        // cout<<"size"<<n<<endl;
        int ans=0;
        int cnt=0;
        bool flg=false;
        int prev=-1;
        for(int i=0;i<n;i++){
            if(flg==false){
                cnt=0;
                while(i<n&&cnt<k&&prev!=v[i]){

                        prev=v[i];
                        i+=1;
                        cnt+=1;

                }
                if(cnt==k){
                    // cout<<"yha "<<i<<" "<<prev<<endl;
                    ans+=1;
                    flg=true;
                    i-=1;
                }
                else{
                    // cout<<"same"<<i<<endl;
                    flg=false;
                    cnt=0;
                    i-=1;
                    prev=-1;
                }
            }
            else{
                // cout<<i<<" "<<prev<<" "<<v[i]<<endl;
                if(v[i]!=prev){
                    while(i<n&&v[i]!=prev){
                    ans+=1;
                    prev=v[i];
                    i+=1;
                 }
                    flg=false;
                    cnt=0;
                    prev=-1;
                    if(i<n)i-=1;
                    else break;
                }
                else{
                    flg=false;
                    cnt=0;
                    prev=-1;
                    i-=1;
                }
            }
        }
        return ans;

    }
};
