class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size = 1 , need = !colors[0] , ans = 0 , n = colors.size();
        for(int i = 1 ; i<n+k-1 ; i++){
            if(colors[i%n]==need){
                need = !need;
                size++;
            }else{
                size = 1;
                need = !colors[i%n];
            }
            if(size>=k) ans++;
        }
        return ans;
    }
};
