class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size();
    int c = 0;
    int a = 0;
    for (int i = 0; i < n + k - 1; ++i) {
      if (a < 1) ++a;
      else {
        int j = ((i - 1) % n + n) % n;
        if (colors[i % n] != colors[j]) ++a; else a = 1;
      }
      if (a >= k) ++c;
    }
    return c;
  }
};
