class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0; i<k-1; i++){
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        vector<int>dp(n+1,0);
        dp[1]=1;
        for(int i=1; i<n; i++){
            if(colors[i]!=colors[i-1]){
                dp[i+1]=1+dp[i];
            }
            else{
                dp[i+1]=1;
            }
        }
        int ans=0;
        for(int i=k-1; i<n; i++){
            if(dp[i+1]>=k){
                ans++;
            }
        }
        return ans;
    }
};
