class Solution {
public:

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),cnt=0,res=1,i=1;
        vi col(all(colors));
        for(auto it:colors){
            col.pb(it);
        }
        while(i<n+k-1){
            if(col[i]!=col[i-1]){
                res++;
            }else{
                res=1;
            }
            if(res>=k) cnt++;
            i++;
        }

        return cnt;


    }
};
