class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> nums;
        for (int i : colors)
            nums.push_back(i);
        for (int i : colors)
            nums.push_back(i);
        int j = 0, ans = 0;
        for (int i = 0;i < n;i++) {
            if (j < i)
                j = i;
            while ((j + 1 < 2 * n) && (nums[j] != nums[j + 1])) {
                j++;
            }
            int len = (j - i + 1);
            if (len >= k)
                ans++;
            //cout << i << ' ' << j << ' ' << ans << '\n';
        }
        return ans;
    }
};
