class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
         int n = arr.size();
        int seqCount = 0;

        if (k == 1) {
            return n;
        }

        int currentLength = 1;
        int idx = 1;

        while (idx < n + k - 1) {
            int curr = idx;
            int prev = idx - 1;

            if (curr >= n) {
                curr -= n;
            }
            if (prev >= n) {
                prev -= n;
            }

            if (arr[curr] != arr[prev]) {
                ++currentLength;
            } else {
                currentLength = 1;
            }

            if (currentLength >= k) {
                ++seqCount;
            }

            ++idx;
        }

        return seqCount;
    }
};
