class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        b = a + a
        f = [0] * len(b)
        for i in range(1, len(b)):
            f[i] = f[i - 1] + (b[i] != b[i - 1])
        ans = 0
        for i in range(len(a)):
            ans += f[i + k - 1] - f[i] == k - 1
        return ans
