#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        int res = 0, n = colors.size();
        int cnt = 0;
        for (int i = 1; i < k - 1; i++) {
            if (colors[i] == colors[i - 1]) {
                cnt++;
            }
        }
        for (int l = 0, r = k - 1; r < n + k - 1; r++) {
            if (colors[r % n] == colors[(r - 1 + n) % n]) {
                cnt++;
            }
            if (cnt == 0) res++;
            if (colors[l % n] == colors[(l + 1) % n]) {
                cnt--;
            }
            l++;
        }
        return res;
    }
};
