class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int last=colors[0];
        int i=0;
        int len =1;
        int ans =0;
        for(int m = 1 ;m<colors.length+k-1;m++){
            int j = (m)%colors.length; 
            if(colors[j]!=last){
                len++;
                if(len==k){
                    System.out.println(m);
                    ans++;
                    len--;
                }
            }else{
                len=1;

            }
            last = colors[j];
        }
        return ans;
    }
}
