class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
          int n = arr.size();
        vector<int> color(n + k - 1);

        for (int i = 0; i < n; ++i) {
            color[i] = arr[i];
        }
        for (int i = 0; i < k - 1; ++i) {
            color[n + i] =arr[i];
        }

        int ans = 0;
        int ct = 1;
        for (int i = 1; i < color.size(); ++i) {
            if (color[i] != color[i - 1]) {
                ++ct;
            } else {
                ct = 1;
            }
            if (ct >= k) {
                ++ans;
            }
        }

        return ans;
    }
};
