#include <bits/stdc++.h>
#define ll long long int
#define lp(i, a, b) for (ll i = a; i < b; i++)
#define lpa(i, a, b, x) for (ll i = a; i < b; i += x)
#define pl(i, a, b) for (ll i = a; i >= b; i--)
#define pb push_back
#define ms(a, x) memset(a, x, sizeof(a))
#define all(v) v.begin(), v.end()
#define pll pair<ll, ll>
#define vpll vector<pll>
#define vll vector<ll>
#define vvll vector<vll>
#define ff first
#define ss second
class Solution {
public:
    int numberOfAlternatingGroups(std::vector<int>& col, int grp) {
        int siz = col.size();
        vector<int> nco(siz + grp - 1);
        lp(i, 0, siz)  nco[i] = col[i];
        lp(i, 0, grp - 1) nco[siz + i] = col[i];
        ll res = 0 , len = 1;
        lp(i, 1, nco.size()) 
        {
            if (nco[i] != nco[i - 1]) len++;
            else len = 1;
            if (len >= grp)  res++;
        }
        return res;
    }
};
