class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v1, int k) {
        int ans=0,n=v1.size();
        queue<int>q;
        for(int i=0;i<n;i++)
        {
            q.push(v1[i]);
        }
        for(int i=0;i<k-1;i++)
        {
            q.push(v1[i]);
        }
        int cnt=0,prev=-1;
        while(q.size())
        {
            if(q.front()!=prev)
            {
                cnt++;
                if(cnt==k)
                {
                    ans++;
                    cnt--;
                }
                prev=q.front();
                q.pop();
            }
            else
            {
                prev=q.front();
                q.pop();
                cnt=1;
            }
        }
        return ans;
    }
};
