class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int res = 0, len = 1, i = 1;
        while(i<n+k-1)
        {
            if(colors[i%n]!=colors[(i-1)%n])
            len++;
            else
            len=1;

            if(len>=k)
            res++;
            i++;
        }
        return res;
    }
};
