class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        # self.colors = colors
        n=len(colors)
        # self.n = len(colors)
        mismatch = deque()
        for i in range(k-1):
            if colors[i]==colors[i+1]:
                mismatch.append(i)
        # print(mismatch)
        res = 1 if len(mismatch)==0 else 0
        for i in range(n-1):
            if len(mismatch)>0 and mismatch[0]==i:
                mismatch.popleft()
            if colors[(i+k-1)%n]==colors[(i+k)%n]:
                mismatch.append((i+k-1)%n)
            if len(mismatch)==0:
               res += 1
            # print(mismatch)
        return res

