class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> new_colors(2 * n);
        for(int i = 0; i < n;i++) {
            new_colors[i] = colors[i];
            new_colors[i + n] = colors[i];
        }
        int l = 0, r = 0, res = 0;
        for(r = 1; r < n + k - 1; r++) {
            if(new_colors[r] == new_colors[r - 1]) {
                l = r;
            }
            if(r - l + 1 >= k) {
                //cout << l << " " << r << endl;
                res += 1;
            }
        }
        return res;
    }
};
