class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k)
    {
        for (int i = 0; i < k-1; i++) colors.push_back(colors[i]);
        int const n = colors.size();

//         for (int color : colors) cout<<color<<" ";
//         cout<<endl;

        int ans = 0;
        int i = 0, j = 1;
        while (j <= n-1)
        {
            // cout<<"i: "<<i<<" j: "<<j<<" ans: "<<ans<<endl;
            bool isValid = true;
            while (j <= n-1 && j-i+1 <= k)
            {
                // cout<<j<<" ";
                if (colors[j] == colors[j-1])
                {
                    isValid = false;
                    break;
                }

                j++;
            }
            // cout<<endl;

            if (isValid && j-i == k)
            {
                ans++;
                i++;
            }
            else
            {
                i = j;
                j++;
            }
        }

        return ans;

    }
};
