class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for (int i = 0; i < k; i++) {
            c.push_back(c[i]);
        }
        int res = 0, fl = 0;
        int i = 0;

        while (i < n) {
            if (fl == 0) {
                int t = 1, j = i;
                for (j = i; j < i + k - 1; j++) {
                    if (c[j] == c[j + 1]) {
                        t = 0;
                        i = j + 1;
                        fl = 0;
                        break;
                    }
                }
                if (t == 1) {
                    res++;
                    fl = 1;
                    i++;
                }
            } else { // fl==1
                if (c[i + k - 2] != c[i + k - 1]) {
                    res++;
                    i++;
                } else {
                    fl = 0;
                    i = i + k - 1;
                }
            }
        }
        return res;
    }
};
