class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=arr.size();

        vector<int> vec(n+k-1);
        for (int i=0; i<k-1; i++) vec[n+i]=arr[i];
        for (int i=0; i<n; i++) vec[i]=arr[i];

        int n2=1, ans=0;
        for (int i=1; i<vec.size(); i++) {
            if (vec[i]!=vec[i - 1]) n2++;
            else n2=1;

            if (n2>=k) ans++;
        }

        return ans;
    }
};
