class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0 ;i<k;i++) colors.push_back(colors[i]);
        int ans =0 ;
        int i =0 , j =0 ;
        while(i<colors.size() && j<colors.size()-1){
            // cout << i << " " << j << " "  << ans << endl;
            if(j-i+1==k){
                ans++;
                i++;
            }
            if(colors[j]==colors[j+1]){
                i=j+1;
            }
            j++;
        }
        // if(j-i+1==k)ans++;
        return ans;
    }
};
