#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n == 0) return 0;

        int i = 0, j = 0, count = 0;
        int ans = 0;

        while (j < n && i < n + k - 1) {
            if (colors[(i + 1) % n] != colors[i % n]) {
                count++;
                while(count > k){
                    if((colors[(j + 1) % n] != colors[j % n])){
                        count--;
                    }
                    j++;
                }

                if (count == k) {
                    // cout<<"i - "<<i<<endl;
                    ans++;
                    // count--;  // Continue looking for the next group
                }
            } else {
                if(count>=k-1) ans++;
                count = 0; // Reset the count if it's not alternating
                j = i;
                if((colors[(j + 1) % n] != colors[j % n])){
                        count++;
                    }

            }
            // cout<<i<<" i - count - "<<count<<endl;
            i++;

        }
        return ans;
    }
};
